query:
query shoppingList($input: GetShoppingListInput!, $pagination: PaginationInput!) {
  shoppingList(input: $input, pagination: $pagination) {
        key            
    id
       version
    shareLink
    audit {
      createdAt
      createdBy
    }
    lineItems {
      id
      addedAt
      product {
        id
        productDetails {
          productSku {
            listPrice
            productName
            brandName
            salePrice
            skuImages {
              image250
            }
            skuId
            variationDesc
            variationTypeDisplayName
            variationValue
            displayName
            primarySkinTone
            isWithBackInStockTreatment
            starRatings
            reviewsCount
            actionFlags {
              isShowEmailMeWhenBackInStore
              isShowChangeEmailMeWhenBackInStore
              isShowBuyNow
              isAddToBasket
              backInStockPhoneReminderNumber
              backInStockPhoneReminderStatus
              backInStockReminderEmail
              backInStockReminderStatus
              isBuyNow
              isRestrictedCountry              
            }
          }
        }
        attributes {
          name
          value
        }
        name
        brand {
          id
          name
        }
        category
        type
        sku {
          id
          image
          price {
            currency
            amount
          }
          salePrice {
            currency
            amount            
          }
          size          
        }
      }
    }
  }
}
