query:
mutation removeItemTest($removeItemInput: RemoveItemInput!) {
  removeItem(removeItemInput: $removeItemInput) {
    id
    key
    version
    audit {
      createdAt
      createdBy
    }
    lineItems {
      id
      addedAt
      product {
        id
        name
        brand {
          id
          name
        }
        type
        category
        sku {
          id
          price {
            currency
            amount
          }
          salePrice {
            currency
            amount
          }
          size
          images {
            image62
            image97
            image135
            image162
            image250
            image450
          }
          variant {
            type
            name
            value
          }
        }
      }
    }
  }
}
